import { useState } from "react";
import Logo from "./Logo";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: true },
  { id: 3, description: "Charger", quantity: 1, packed: false },
];

export default function App() {
  const [items, setItems] = useState([]);
  // const [numItems, setNumItems] = useState(0); // Unnecessary
  // const numItems = items.length; //as soon as state changes, component will get rerendered and this code wil be executed
  // But instead doing that here, we can do this in the componet where we need to cosume it to avoid passing props
  function handleAddItems(item) {
    setItems((items) => [...items, item]);
    // setNumItems((num) => num + 1); // This state and operation is unnecessary because it a computed state
  }

  function handleDeleteItem(id) {
    setItems((items) => items.filter((item) => item.id !== id));
  }

  function handleToggleItem(id) {
    console.log("handle toggle id " + id);
    setItems((items) =>
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }

  //OR
  // function handleToggleItem(id) {
  //   console.log("handle toggle id " + id);
  //   setItems((items) =>
  //     items.map((item) =>{
  //       return item.id === id ? { ...item, packed: !item.packed } : item // If we are wrapping array callback function in curly braces, then we explicitly need to add return keyword
  // })
  //   );
  // }

  function handleClearList() {
    const confirmed = window.confirm(
      "Are you sure you want to delete all items?"
    ); //This is to add a confirmation message before deleting
    if (confirmed) setItems([]);
  }

  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItems} />
      <PackingList
        items={items}
        onDeleteItem={handleDeleteItem}
        onToggleItems={handleToggleItem}
        onClearList={handleClearList}
      />
      <Stats items={items} />
    </div>
  );
}

function Form({ onAddItems }) {
  const [description, setDescription] = useState(""); //Here react is setting description to empty string
  // even if we are changing the input, hence we need handleChange function
  const [quantity, setQuantity] = useState(1);

  function handleSubmit(e) {
    e.preventDefault(); //To stop page auto refreshing after submit

    if (!description) return; // We shouldn't be able to submit with blank description

    const newItem = { description, quantity, packed: false, id: Date.now() };

    onAddItems(newItem);

    setDescription("");
    setQuantity(1); //Setting elements back to their initial state after submitting
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      {/* We can also listen to onclick event of button but it will not work after hitting enter key,
      Hence it is important to listen to on submit of form here */}
      <h3>What do you need for your üòç trip?</h3>
      <select
        value={quantity}
        onChange={(e) => setQuantity(Number(e.target.value))}
      >
        {/* e.target.value will always be a string but we want a number there */}
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      {/* To make array of X elements */}
      <input
        type="text"
        placeholder="Item..."
        value={description} // DOM is not incharge of this value anymore
        onChange={(e) => setDescription(e.target.value)} // This updates the state with actual value
      />
      <button>Add</button>
    </form>
  );
}

function PackingList({ items, onDeleteItem, onToggleItems, onClearList }) {
  const [sortBy, setSortBy] = useState("input");

  let sortedItems;

  if (sortBy === "input") sortedItems = items;

  if (sortBy === "description")
    sortedItems = items
      .slice() // Without .slice() here, the original array will also get sorted which we don't want
      .sort((a, b) => a.description.localeCompare(b.description));

  if (sortBy === "packed")
    sortedItems = items
      .slice() // Without .slice() here, the original array will also get sorted which we don't want
      .sort((a, b) => Number(a.packed) - Number(b.packed));

  return (
    <div className="list">
      <ul>
        {sortedItems.map((item) => (
          <Item
            item={item}
            onDeleteItem={onDeleteItem}
            onToggleItems={onToggleItems}
            key={item.id}
            hand
          />
        ))}
      </ul>

      <div className="actions">
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="input">Sort by input order</option>
          <option value="description">Sort by description</option>
          <option value="packed">Sort by packed status</option>
        </select>
        <button onClick={onClearList}>Clear List</button>
      </div>
    </div>
  );
}

function Item({ item, onDeleteItem, onToggleItems }) {
  return (
    <li>
      <input
        type="checkbox"
        value={item.packed}
        onChange={() => onToggleItems(item.id)}
      />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity}
        {item.description}
      </span>
      <button onClick={() => onDeleteItem(item.id)}>‚ùå</button>
      {/* Here the function is passed through props and then we are updating the state through that function */}
    </li>
  );
}

function Stats({ items }) {
  if (!items.length)
    return (
      <p className="stats">
        <em>Start adding some items to your packing list üöÄ</em>
      </p>
    ); //This is an example of early return which can be usead instead of below method

  const numItems = items.length;
  const numPacked = items.filter((item) => item.packed).length;
  const percentage = Math.round((numPacked / numItems) * 100);
  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? "You got everything! Ready to go ‚úàÔ∏è"
          : `üëú You have ${numItems} items on your list, and you already packed
        ${numPacked} (${percentage}%)`}
      </em>
    </footer>
  );
}
